import { onMount } from 'svelte';

  // State for authentication
  let isLoggedIn = false;
  let username = '';
  let password = '';
  let errorMessage = '';
  let isCreatingAccount = false;

  // State for budget tracker
  let totalIncome = 0;
  let totalExpenses = 0;
  let remainingBudget = 0;
  let transactions = [];

  let type = 'income';
  let amount = 0;

  // Check if user is already logged in
  onMount(() => {
    const user = localStorage.getItem('user');
    if (user) {
      isLoggedIn = true;
      loadUserData(JSON.parse(user));
    }
  });

  // Load user data from localStorage
  function loadUserData(user) {
    totalIncome = user.totalIncome || 0;
    totalExpenses = user.totalExpenses || 0;
    remainingBudget = user.remainingBudget || 0;
    transactions = user.transactions || [];
  }

  // Save user data to localStorage
  function saveUserData() {
    const user = {
      username,
      totalIncome,
      totalExpenses,
      remainingBudget,
      transactions,
    };
    localStorage.setItem('user', JSON.stringify(user));
  }

  // Handle login
  function login() {
    const user = JSON.parse(localStorage.getItem(username));
    if (user && user.password === password) {
      isLoggedIn = true;
      loadUserData(user);
      errorMessage = '';
    } else {
      errorMessage = 'Invalid username or password.';
    }
  }

  // Handle account creation
  function createAccount() {
    if (localStorage.getItem(username)) {
      errorMessage = 'Username already exists.';
      return;
    }
    const user = {
      username,
      password,
      totalIncome: 0,
      totalExpenses: 0,
      remainingBudget: 0,
      transactions: [],
    };
    localStorage.setItem(username, JSON.stringify(user));
    localStorage.setItem('user', JSON.stringify(user));
    isLoggedIn = true;
    errorMessage = '';
  }

  // Handle logout
  function logout() {
    isLoggedIn = false;
    username = '';
    password = '';
    localStorage.removeItem('user');
  }

  // Budget tracker functions
  function addTransaction() {
    if (amount <= 0 || isNaN(amount)) {
      alert('Please enter a valid amount.');
      return;
    }

    if (type === 'income') {
      totalIncome += amount;
    } else if (type === 'expense') {
      totalExpenses += amount;
    }

    remainingBudget = totalIncome - totalExpenses;

    transactions = [
      ...transactions,
      { type, amount, id: Date.now() }
    ];

    amount = 0; // Reset input
    saveUserData();
  }

  function removeTransaction(id) {
    const transaction = transactions.find(t => t.id === id);
    if (transaction.type === 'income') {
      totalIncome -= transaction.amount;
    } else if (transaction.type === 'expense') {
      totalExpenses -= transaction.amount;
    }

    remainingBudget = totalIncome - totalExpenses;

    transactions = transactions.filter(t => t.id !== id);
    saveUserData();
  }

